            migrationBuilder.Sql("INSERT INTO Jogos (CategoriaId, DescricaoCurta, DescricaoDetalhada, EmEstoque, ImagemThumbnailUrl, ImagemUrl, IsJogoPreferido, Nome, Preco, Plataformas, Genero, ClassificacaoEtaria, ClassificacaoEtariaImagemUrl, InformacoesExtras, ImagensAdicionais) VALUES (7, 'Resident Evil Village é um jogo de terror e sobrevivência que segue Ethan Winters em uma vila misteriosa repleta de perigos e criaturas assustadoras.', 'Resident Evil Village é o oitavo título principal da famosa franquia de terror da Capcom. O jogo segue Ethan Winters, que viaja para uma vila remota em busca de sua filha desaparecida. Lá, ele enfrenta terrores indescritíveis, incluindo licantropos, vampiros e outras criaturas sombrias. Village combina elementos clássicos de terror com ação, apresentando gráficos impressionantes e um mundo detalhado. Com uma história envolvente, atmosfera assustadora e combates intensos, o jogo mantém os jogadores na ponta da cadeira enquanto exploram segredos sombrios e enfrentam inimigos aterrorizantes.', 1, 'https://image.api.playstation.com/vulcan/ap/rnd/202101/0812/uAhJNbCgYUmUOuCDiMNXtYBC.png', 'https://image.api.playstation.com/vulcan/ap/rnd/202101/0812/FkzwjnJknkrFlozkTdeQBMub.png', 0, 'Resident Evil Village', 259.99, 'PS4', 'Terror', '+18', 'https://logodownload.org/wp-content/uploads/2017/07/classificacao-18-anos-logo-1.png', 'Terror imersivo: Atmosfera assustadora com gráficos de última geração. História intrigante: Explore segredos sombrios em uma vila misteriosa. Combates intensos: Enfrente criaturas aterrorizantes usando armas e estratégias. Exploração envolvente: Resolva quebra-cabeças e descubra locais icônicos. Personagens marcantes: Interaja com figuras como Lady Dimitrescu e outros vilões memoráveis.', 'https://t2.tudocdn.net/632034?w=1920&h=1080')");

